// 별점
$(document).ready(function(){
	$(".star_open").click(function(){
		if($(".star_option ").css("display") == "none"){
			$('.star_option').show();
			$('.star_option').animate({
				height: "145px"
			}, 200);
		} else {
			$('.star_option').animate({
				height: "0px"
			}, 200);
			$('.star_option').hide({
			}, 10);
		}
	})
});
$(document).ready(function(){
	$(".star_option input").on('change', function() {
		var selectStar_name = $('.star_option input:checked').val();
		$('.star_top').addClass("txt_none");
		$('.star_top').removeClass("star_01");
		$('.star_top').removeClass("star_02");
		$('.star_top').removeClass("star_03");
		$('.star_top').removeClass("star_04");
		$('.star_top').removeClass("star_05");
		$('.star_top').addClass(selectStar_name);
		$('.star_option').animate({
			height: "0px"
		}, 200);
		$('.star_option').hide({
		}, 10);
	});
});


// 팝업


$(document).ready(function(){
	$(".pop_open_btn").bind('click',function(event){
		$(".seok_pop_wrap").fadeOut(10);
		$(".seok_pop").empty();
		var PopTarget = $(this).attr("pop-target");
		var PopTargetClass = "."+ PopTarget;
		var PopTargetHtml = "<div class='"+PopTarget+"'>" + $(PopTargetClass).html() + "</div>";
		// alert(PopTargetHtml);
		$(".seok_pop").append(PopTargetHtml);
		$(".seok_pop_wrap").fadeIn();
		var SeokH = $('.seok_pop').height() ;
		var SeokW = $('.seok_pop').width() ;
		$(".seok_pop_box").css( { "margin-top" : - SeokH/2 } );
		$(".seok_pop_box").css( { "margin-left" : - SeokW/2 } );
		event.preventDefault();
	});
	$(".seok_pop_bg").bind('click',function(event){
		$(".seok_pop_wrap").fadeOut();
		$(".seok_pop").empty();
		event.preventDefault();
	});
	$('.seok_pop_box').on('click', '.pop_open_btn', function() {
		$(".seok_pop_wrap").fadeOut(10);
		$(".seok_pop").empty();
		var PopTarget = $(this).attr("pop-target");
		var PopTargetClass = "."+ PopTarget;
		var PopTargetHtml = "<div class='"+PopTarget+"'>" + $(PopTargetClass).html() + "</div>";
		// alert(PopTargetHtml);
		$(".seok_pop").append(PopTargetHtml);
		$(".seok_pop_wrap").fadeIn();
		var SeokH = $('.seok_pop').height() ;
		var SeokW = $('.seok_pop').width() ;
		$(".seok_pop_box").css( { "margin-top" : - SeokH/2 } );
		$(".seok_pop_box").css( { "margin-left" : - SeokW/2 } );
		event.preventDefault();
	});

	$(".file_img_wrap .file_input").change(function() {
		readURL(this);
	});

	$('.imagebtn').click (function(){
		var ImageUrl = $(this).attr('src');
		$('.p-img').attr('src', ImageUrl);
		$('.img-popup').bPopup({
			modalClose: true
			, opacity: 0.8
			, positionStyle: 'absolute' 
			, speed: 300
			, transition: 'fadeIn'
			, transitionClose: 'fadeOut'
			, zIndex : 99997
		});
	});
});


$(function(){

if($(window).width()<1230) {
}else {
		
}
var swiper = new Swiper('.top_nav .Swiper', {
	slidesPerView: 'auto',
	preventClicks: true,
	preventClicksPropagation: false,
	observer: true,
	observeParents: true
});
var $SwiperItem = $('.top_nav .Swiper .swiper-wrapper .swiper-slide a');
$SwiperItem.click(function(){
	var target = $(this).parent();
	$SwiperItem.parent().removeClass('on')
	target.addClass('on');
	muCenter(target);
});


});
function muCenter(target){
	var snbwrap = $('.top_nav .Swiper .swiper-wrapper');
	var targetPos = target.position();
	var box = $('.top_nav  .Swiper');
	var boxHarf = box.width()/2;
	var pos;
	var listWidth=0;
	
	snbwrap.find('.swiper-slide').each(function(){ listWidth += $(this).outerWidth(); })
	
	var selectTargetPos = targetPos.left + target.outerWidth()/2;
	if (selectTargetPos <= boxHarf) { // left
		pos = 0;
	}else if ((listWidth - selectTargetPos) <= boxHarf) { //right
		pos = listWidth-box.width();
	}else {
		pos = selectTargetPos - boxHarf;
	}
	
	setTimeout(function(){snbwrap.css({
		"transform": "translate3d("+ (pos*-1) +"px, 0, 0)",
		"transition-duration": "500ms"
	})}, 200);
}

function readURL(input) {
	if (input.files && input.files[0]) {
		var reader = new FileReader();
		reader.onload = function(e) {
			var thisBumo = $(input).closest('.file_img_all_wrap');
			thisBumo.find(".file_img_wrap > img").attr('src', e.target.result);
		}
		reader.readAsDataURL(input.files[0]);
	}
}

	
